mapper.select:
SELECT GNR_CD AS gnrCd , DTL_CD AS dtlCd , DTL_NM AS dtlNm , IFNULL(DTL_CTS,'') AS dtlCts , CAPTION AS caption , USE_YN AS useYn FROM gnrcd_dtl WHERE 1=1
AND GNR_CD = #{gnrCd}
ORDER BY DTL_CD

mapper.insert:
  INSERT INTO gnrcd_clf
              ( GNR_CD
                , GNR_NM
                , LENGTH
                , USE_YN
		       ) VALUES 
				 ( #{gnrCd}
				   , #{gnrNm}
				   , #{length}
				   , #{useYn}					 
				 )

  INSERT INTO gnrcd_dtl
              ( GNR_CD
                , DTL_CD
                , DTL_NM
                , DTL_CTS
                , CAPTION
                , USE_YN
		       ) VALUES 
				 ( #{gnrCd}
				   , #{dtlCd}
				   , #{dtlNm}
				   , #{dtlCts}
				   , #{caption}
				   , #{useYn}					 
				 )

mapper.update:
   UPDATE gnrcd_clf
	  SET GNR_NM = #{gnrNm}
	      , LENGTH = #{length}
	      , USE_YN = #{useYn}
	WHERE GNR_CD = #{gnrCd}

mapper.select:
SELECT GNR_CD AS gnrCd , DTL_CD AS dtlCd , DTL_NM AS dtlNm , IFNULL(DTL_CTS,'') AS dtlCts , CAPTION AS caption , USE_YN AS useYn FROM gnrcd_dtl WHERE 1=1
AND GNR_CD = #{gnrCd}
AND DTL_CD = #{dtlCd}

mapper.update:
   UPDATE gnrcd_dtl
	  SET DTL_CD = #{dtlCd}
	      , DTL_NM = #{dtlNm}
	      , DTL_CTS = #{dtlCts}
	      , CAPTION = #{caption}
	      , USE_YN = #{useYn}
	WHERE GNR_CD = #{gnrCd}
	  AND DTL_CD = #{preDtlCd}

mapper.select:
SELECT DISTINCT MVGR3 AS mvgr3 , MVGT3 AS mvgt3 FROM prd_srch WHERE 1=1
AND MVGR2 = #{mvgr2}
ORDER BY MVGT3

SELECT DISTINCT NORMT AS normt FROM prd_srch WHERE NORMT != ''
AND MVGR2 = #{mvgr2}
AND MVGR3 = #{mvgr3}
ORDER BY NORMT

SELECT DISTINCT A.matnr , A.komtx , A.charg , 'Y' AS chkYn FROM prd_mst A , prd_by_vnd B WHERE A.matnr = B.matnr AND A.charg = B.charg
AND A.matnr LIKE CONCAT(#{matnr},'%')
AND B.lifnr = #{lifnr}
UNION ALL SELECT C.matnr , C.komtx , C.charg , C.chkYn FROM ( SELECT DISTINCT A.matnr , A.komtx , A.charg , CASE WHEN B.matnr IS NULL THEN 'N' ELSE 'Y' END AS chkYn FROM prd_mst A LEFT OUTER JOIN prd_by_vnd B ON (A.matnr = B.matnr AND A.charg = B.charg
AND B.lifnr = #{lifnr}
) WHERE 1=1
AND A.matnr LIKE CONCAT(#{matnr},'%')
) C WHERE C.chkYn = 'N' ORDER BY matnr LIMIT 0, 99

mapper.insert:
  INSERT INTO prd_by_vnd
              ( lifnr
                , matnr
                , charg
                , credat
                , cretim
                , creusr
		       ) VALUES 
				 ( #{lifnr}
				   , #{matnr}
				   , #{charg}
	   		       , DATE_FORMAT(now(), '%Y%m%d')
	   		       , DATE_FORMAT(now(), '%H%i%s')   
	   		       , #{crtrId}
				 )

mapper.delete:
      DELETE 
	    FROM prd_by_vnd
	   WHERE lifnr = #{lifnr} 
	     AND matnr = #{matnr}
	     AND charg = #{charg}
